query IndexQuery(
  $route: String
) {
  ...navTree
  allMarkdownRemark(limit: 2000) {
    edges {
      node {
        fields {
          slug
          route
        }
        excerpt
        timeToRead
        frontmatter {
          title
          description
          tags
          category
          rawDate: date
          cover
        }
      }
    }
  }
}

fragment navTree on Query {
  currentPage: allSitePage(filter: {path: {eq: $route}}) {
    edges {
      node {
        tree
        path
        context {
          title
          category
          slug
          route
        }
      }
    }
  }
}

query DocByRoute(
  $route: String!
) {
  ...navTree
  site {
    siteMetadata {
      title
    }
  }
  markdownRemark(fields: {route: {eq: $route}}) {
    html
    tableOfContents(pathToSlugField: "fields.route")
    timeToRead
    excerpt
    frontmatter {
      title
      rawDate: date
      category
      tags
    }
    fields {
      route
      slug
    }
  }
}

